import { Component, OnDestroy, OnInit } from '@angular/core';
import { MovieService } from '../movie.service';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Movie } from '../movies.model';
import { WindRefService } from 'src/app/wind-ref.service';
import { DomSanitizer } from '@angular/platform-browser';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-movies-detail',
  templateUrl: './movies-detail.component.html',
  styleUrls: ['./movies-detail.component.css']
})
export class MoviesDetailComponent implements OnInit, OnDestroy {

  movie!: Movie;
  id!: string;
  nativeWindow: any;
  subscription!: Subscription;

  constructor(
    private movieService: MovieService,
    private windowRefService: WindRefService,
    private route: ActivatedRoute,
    public sanitizer: DomSanitizer,
    private router: Router) { }

  ngOnInit() {

    this.route.params.subscribe(
      (params: Params) => {
        this.id = params['id'];
        this.movie = this.movieService.getMovie(this.id);

        if (this.movie == null) {
          this.subscription = this.movieService.movieListChangedEvent.subscribe(() => {
            this.movie = this.movieService.getMovie(params['id'])
          }
          )
        }
      })

    this.nativeWindow = this.windowRefService.getNativeWindow();


  }


  onDelete() {
    this.movieService.deleteMovie(this.movie);
    this.router.navigate(['/movies'], { relativeTo: this.route });
  }


  // TO CHANGE THIS WITH REAL DATA
  onEdit() {

  }

  ngOnDestroy(): void {
    if (this.subscription)
      this.subscription.unsubscribe();
  }
}
<div class="panel panel-default" xmlns="http://www.w3.org/1999/html">
  <div class="panel-heading">
    <div class="panel-heading">
      <h4 class="title margin-left-right">{{ document.name }}</h4>
      <div class="row">
        <span class="pull-left">
          <img
            src="{{ document.image }}"
            alt="{{ document.name }}"
            class="img-fluid d-block mx-auto"
            style="max-width: 100%"
          />
        </span>
        <span class="pull-right btn-toolbar" style="margin-top: 35px">
          <div class="row pull-right margin-left-right">
            <!-- <a class="btn btn-primary" (click)="onView()">Trailer</a> -->
            <a
              [routerLink]="['/documents', document.id, 'edit']"
              class="btn btn-info"
              (click)="onEdit()"
              >Edit</a
            >
            <a class="btn btn-danger" (click)="onDelete()">Delete</a>
          </div>
        </span>
      </div>
    </div>
    <div class="panel-body">
      <div class="pad-left-right">
        <div class="row">
          <label>Description:</label>
          <br />
          <span class="label-value">{{ document.description }}</span>
          <hr />
        </div>
      </div>
      <div class="pad-left-right">
        <div class="row">
          <label>Trailer:</label>
          <br />
          <div class="videoWrapperOuter">
            <div class="videoWrapperInner">
              <iframe
                [src]="
                  sanitizer.bypassSecurityTrustResourceUrl(document.trailerUrl)
                "
                frameborder="0"
                allowfullscreen
              ></iframe>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
