import { Component, EventEmitter, OnDestroy, OnInit, Output } from '@angular/core';
import { Movie } from '../movies.model';
import { MovieService } from '../movie.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-movies-list',
  templateUrl: './movies-list.component.html',
  styleUrls: ['./movies-list.component.css']
})
export class MoviesListComponent implements OnInit, OnDestroy {
  @Output() selectedMovieEvent = new EventEmitter<Movie>();

  term: String = '';


  movies: Movie[] = [];

  private subscription!: Subscription;

  constructor(private movieService: MovieService) { }

  ngOnInit(): void {


    this.subscription = this.movieService.movieListChangedEvent.subscribe(
      (movies: Movie[]) => {
        this.movies = movies;
      }
    )

    this.movieService.getMovies();
  }


  // TO CHANGE THIS WITH REAL DATA

  onAdd() {

  }

  search(value: String) {

    this.term = value;
  }


  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

}
<div class="panel panel-default">
  <div class="panel-heading">
    <div class="row pad-left-right">
      <span class="pull-left title">Movies</span>
      <a
        class="btn btn-success pull-right"
        [routerLink]="['new']"
        (click)="onAdd()"
        >Add Movie</a
      >
      <br />
      <hr />
      <div class="input-group pull-left col-xs-4">
        <input
          type="text"
          ngModel
          (keyup)="search(searchBox.value)"
          placeholder="Look for a movie ..."
          #searchBox="ngModel"
          class="form-control"
        />
        <div (click)="search(searchBox.value)"></div>
      </div>
    </div>
  </div>

  <div class="panel-body">
    <app-movies-item
      *ngFor="
        let movieEl of movies | moviesFilter : searchBox.value;
        let i = index
      "
      [movie]="movieEl"
    ></app-movies-item>
  </div>
</div>
