import { Component, Input, OnInit } from '@angular/core';
import { Contact } from '../contacts.model';
import { ContactService } from '../contact.service';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Subscription } from 'rxjs';
import { DomSanitizer } from '@angular/platform-browser';
import { ContactListComponent } from '../contact-list/contact-list.component';


@Component({
  selector: 'app-contact-detail',
  templateUrl: './contact-detail.component.html',
  styleUrls: ['./contact-detail.component.css']
})
export class ContactDetailComponent implements OnInit {
  contact: any = [];
  subscription!: Subscription;
  id: any;

  constructor(private contactService: ContactService, private contactListComponent: ContactListComponent, private router: Router, private route: ActivatedRoute, public sanitizer: DomSanitizer, private http: HttpClient) {
  }

  ngOnInit(): void {


    // this.route.params.subscribe(params => {

    //   this.contact = this.contactService.getContact(params['id']);
    //   console.log('Movie returned: ' + this.contact)

    //   if (this.contact == null) {
    //     this.subscription = this.contactService.contactListChangedEvent.subscribe(() => {
    //       this.contact = this.contactService.getContact(params['id']);
    //     }
    //     )
    //   }
    // })



  }

  getContact(id: string) {
    const url = 'https://api.themoviedb.org/3/'
    const apiKey = 'f4ec058abc3d746728270f736f4851fd'
    console.log("`${url}movie/${id}?api_key=${apiKey}`" + `${url}movie/${id}?api_key=${apiKey}`)
    return this.http.get(`${url}movie/${id}?api_key=${apiKey}`);
  }


  onDelete() {
    this.contactService.deleteContact(this.contact);

    this.router.navigateByUrl('/contacts');
  }


  ngOnDestroy(): void {
    if (this.subscription)
      this.subscription.unsubscribe();
  }

}
