import { Component, EventEmitter, OnDestroy, OnInit, Output } from '@angular/core';
import { Document } from '../documents.model';
import { DocumentService } from '../document.service';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-documents-list',
  templateUrl: './documents-list.component.html',
  styleUrls: ['./documents-list.component.css']
})
export class DocumentsListComponent implements OnInit, OnDestroy {
  @Output() selectedDocumentEvent = new EventEmitter<Document>();

  term: String = '';


  documents: Document[] = [];

  private subscription!: Subscription;

  constructor(private documentService: DocumentService) { }

  ngOnInit(): void {
    // TO DELETE THE FOLLOWING LINE WHEN READY TO USE FIREBASE
    // this.documents = this.documentService.getDocuments();

    this.subscription = this.documentService.documentListChangedEvent.subscribe(
      (documents: Document[]) => {
        this.documents = documents;
      }
    )

    // USE THE FOLLOWING TO GET DOCUMENTS FROM FIREBASE
    this.documentService.getDocuments();
  }


  // TO CHANGE THIS WITH REAL DATA
  // this function is add a new document using hard coded details
  onAdd() {

    // fake add document 
    // let testDocument = new Document(this.documentService.getMaxId().toString(), 'The Book of Mormon', 'Another Testament of Jesus Christ', 'https://www.churchofjesuschrist.org/study/scriptures/bofm?lang=eng', 'child')
    // this.documentService.addDocument(testDocument);

  }

  search(value: String) {

    this.term = value;
  }


  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

}
<div class="panel panel-default">
  <div class="panel-heading">
    <div class="row pad-left-right">
      <span class="pull-left title">Movies</span>
      <a
        class="btn btn-success pull-right"
        [routerLink]="['new']"
        (click)="onAdd()"
        >Add Movie</a
      >
      <br />
      <hr />
      <div class="input-group pull-left col-xs-4">
        <input
          type="text"
          ngModel
          (keyup)="search(searchBox.value)"
          placeholder="Look for a movie ..."
          #searchBox="ngModel"
          class="form-control"
        />
        <div (click)="search(searchBox.value)"></div>
      </div>
    </div>
  </div>

  <div class="panel-body">
    <div class="row"></div>
    <app-documents-item
      *ngFor="
        let documentEl of documents | moviesFilter : searchBox.value;
        let i = index
      "
      [document]="documentEl"
    ></app-documents-item>
  </div>
</div>
