import { Component, OnInit } from '@angular/core';
import { DocumentService } from '../document.service';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Document } from '../documents.model';
import { WindRefService } from 'src/app/wind-ref.service';

@Component({
  selector: 'app-documents-detail',
  templateUrl: './documents-detail.component.html',
  styleUrls: ['./documents-detail.component.css']
})
export class DocumentsDetailComponent implements OnInit {

  document!: Document;
  id!: string;
  nativeWindow: any;

  constructor(
    private documentService: DocumentService,
    private windowRefService: WindRefService,
    private route: ActivatedRoute,
    private router: Router) { }

  ngOnInit() {
    this.route.params.subscribe(
      (params: Params) => {
        this.id = params['id'];
        this.document = this.documentService.getDocument(this.id);
      })

    this.nativeWindow = this.windowRefService.getNativeWindow();
  }

  onView() {
    if (this.document.trailerUrl) {
      this.nativeWindow.open(this.document.trailerUrl);
    }
  }

  onDelete() {
    this.documentService.deleteDocument(this.document);
    //route back to the '/documents' URL
    this.router.navigate(['/documents'], { relativeTo: this.route });
  }


  // TO CHANGE THIS WITH REAL DATA
  // this function is updating the existing document with hard coded data 
  onEdit() {
    // fake updated contact 
    // let testDocument = new Document('2', 'The Book of Mormon', 'Another Testament of Jesus Christ', 'https://www.churchofjesuschrist.org/study/scriptures/bofm?lang=eng', 'child')

    // this.documentService.updateDocument(this.document, testDocument);
    // this.router.navigate(['/documents'], { relativeTo: this.route });


  }
}
<div class="panel panel-default" xmlns="http://www.w3.org/1999/html">
  <div class="panel-heading">
    <div class="panel-heading">
      <h4 class="title margin-left-right">{{ document.name }}</h4>
      <div class="row">
        <span class="pull-left">
          <img
            src="{{ document.image }}"
            alt="{{ document.name }}"
            class="img-fluid d-block mx-auto"
            style="max-width: 100%"
          />
        </span>
        <span class="pull-left">
          <div class="pad-left-right">
            <div class="row">
              <label>Trailer:</label>
              <br />
              <div class="videoWrapperOuter">
                <div class="videoWrapperInner">
                  <iframe
                    src="https://youtube.com/embed/TnGl01FkMMo"
                    frameborder="0"
                    allowfullscreen
                  ></iframe>
                </div>
              </div>
            </div>
          </div>
        </span>
        <span class="pull-right btn-toolbar" style="margin-top: 35px">
          <div class="row pull-right margin-left-right">
            <!-- <a class="btn btn-primary" (click)="onView()">Trailer</a> -->
            <a
              [routerLink]="['/documents', document.id, 'edit']"
              class="btn btn-info"
              (click)="onEdit()"
              >Edit</a
            >
            <a class="btn btn-danger" (click)="onDelete()">Delete</a>
          </div>
        </span>
      </div>
    </div>
    <div class="panel-body">
      <div class="pad-left-right">
        <div class="row">
          <label>Description:</label>
          <br />
          <span class="label-value">{{ document.description }}</span>
        </div>
      </div>
    </div>
  </div>
</div>
